    private void BotaoExcluir1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotaoExcluir1ActionPerformed
        // TODO add your handling code here:
        int id = getPosicao();
        
        int resposta = JOptionPane.showConfirmDialog(null, "Deseja realmente excluir esse registro?");
        if (resposta == 0) {
          FilmeDAO.excluir(id);
        } 
        
        TabelaDeFilme.setModel(montarTabela(FilmeDAO.listarTodos()));
        ScrollDaTabela.setViewportView(TabelaDeFilme);        
    }//GEN-LAST:event_BotaoExcluir1ActionPerformed

