    private DefaultTableModel montarTabela(List<Filme> lista) {
        String[] colunas = {"ID", "Nome", "Data de lançamento", "Categoria", "Assistido"};
        
        // Criamos a tabela
        DefaultTableModel tabela = new DefaultTableModel(colunas, 0);
       
        //ArrayList<Filme> lista = ListaFilme.Listar();
        
        for (int i=0; i < lista.size(); i++) {
            Filme p = lista.get(i);
            
            String assistido = "";
            if (p.isAssistido()) {
                assistido = "Sim";
            } else {
                assistido = "Não";
            }
            
            String[] linha = {
                Integer.toString(p.getId()),
                p.getNome(),
                Filme.converterParaJava(p.getDataLancamento()),
                p.getCategoria(),
                assistido,
            };
            
            tabela.addRow(linha);
        }
        
        return tabela;
    }
        
    private int getPosicao() {
        int posicao = TabelaDeFilme.getSelectedRow();
        
        if(posicao <= -1) {
            JOptionPane.showMessageDialog(null, "Selecione um item para excluir.");
        }
                
        return Integer.parseInt( (String) TabelaDeFilme.getValueAt(posicao, 0) );
    }
    
